public class SpringPrinter implements DocumentProcessor { public Document processDocument ( Document doc ) throws DocumentProcessorException { ToStringCreator creator = new ToStringCreator ( doc ) ; creator . append ( "id" , doc . getId ( ) ) ; creator . append ( "type" , doc . getType ( ) ) ; creator . append ( "reference" , doc . getReference ( ) ) ; doc . setPrintRepresentation ( creator . toString ( ) ) ; return doc ; } } 