public class BrowsingContext extends AbstractDatatype { public static final BrowsingContext THE_INSTANCE = new BrowsingContext ( ) ; private BrowsingContext ( ) { super ( ) ; } @ Override public void checkValid ( CharSequence literal ) throws DatatypeException { if ( literal . length ( ) == 0 ) { throw newDatatypeException ( "Browsing context name must be at least one character long." ) ; } if ( literal . charAt ( 0 ) == '_' ) { throw newDatatypeException ( "Browsing context name started with the underscore." ) ; } else { return ; } } @ Override public String getName ( ) { return "browsing context name" ; } } 