public class DateOrTime extends AbstractDatetime { public static final DateOrTime THE_INSTANCE = new DateOrTime ( ) ; private static final Pattern THE_PATTERN = Pattern . compile ( "^(?:(?:([0-9]{4,})-([0-9]{2})-([0-9]{2})(?:[T ]([0-9]{2}):([0-9]{2})(?::([0-9]{2})(?:\\.[0-9]{1,3})?)?(?:Z|(?:([+-][0-9]{2}):([0-9]{2})))?)?)|(?:([0-9]{2}):([0-9]{2})(?::([0-9]{2})(?:\\.[0-9]{1,3})?)?(?:Z|(?:([+-][0-9]{2}):([0-9]{2})))?))$" ) ; private DateOrTime ( ) { super ( ) ; } @ Override protected Pattern getPattern ( ) { return THE_PATTERN ; } @ Override public String getName ( ) { return "date or time" ; } } 