public class TaxAdder implements DocumentProcessor { private double taxRate = 0.0d ; public Document processDocument ( Document doc ) throws DocumentProcessorException { double total = doc . getTotalCent ( ) ; total += taxRate * doc . getTotalCent ( ) ; doc . setTotalCent ( Math . round ( total ) ) ; return doc ; } public double getTaxRate ( ) { return taxRate ; } public void setTaxRate ( double taxRate ) { this . taxRate = taxRate ; } } 